--CREATE DATABASE futbol; la he creado con el comando que utilizamos en la practica de futbol

COMMIT;
BEGIN;

CREATE TABLE IF NOT EXISTS Ligas (
    id BIGINT GENERATED BY DEFAULT AS IDENTITY PRIMARY KEY,
    nombre VARCHAR(100) NOT NULL,
    numeroEquipos INT NOT NULL,
    pais VARCHAR(100) NOT NULL
);

CREATE TABLE IF NOT EXISTS Equipos (
    id BIGINT GENERATED BY DEFAULT AS IDENTITY PRIMARY KEY,
    nombre VARCHAR(100) NOT NULL,
    estadio VARCHAR(100),
    entrenador VARCHAR(100),
    ciudad VARCHAR(100),
    fundacion SMALLINT,
    idLiga BIGINT NOT NULL,
    FOREIGN KEY (idLiga) REFERENCES Ligas(id)
        ON DELETE CASCADE
        ON UPDATE CASCADE
);

CREATE TABLE IF NOT EXISTS Jugadores (
    id BIGINT GENERATED BY DEFAULT AS IDENTITY PRIMARY KEY,
    nombre VARCHAR(100) NOT NULL,
    dorsal INT NOT NULL,
    posicion VARCHAR(20) NOT NULL,
    nacionalidad VARCHAR(100),
    fechaNacimiento VARCHAR(100),
    idEquipo BIGINT NOT NULL,
    FOREIGN KEY (idEquipo) REFERENCES Equipos(id)
        ON DELETE CASCADE
        ON UPDATE CASCADE
);

